  };

  const onFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const onDocumentLoadSuccess = (numPages, i) => {
    var j = numPages.numPages;
    var item = "";
    for (i = 0; i < j; i++) {
      item += i + " ";
    }
    var n = item;
    let numStringArr = `${n}`.split("").map((el) => parseInt(el));
    var numStringArrs = numStringArr.filter((e) => e === 0 || e);
    setPagelist(numStringArrs);
  };

  const handlePdf = () => {
    var canvas_data = document.getElementsByClassName("canvas_div_pdf");
    var w = canvas_data.item(0).width
    var h = canvas_data.item(0).height

    // var HTML_Width = $(".canvas_div_pdf").width();
    // var HTML_Height = $(".canvas_div_pdf").height();
