                  </div>
                </div>
              </div>
            )}

            {showtext && (
              <div className="Apps">
                <form>
                  <input
                    type="text"
                    maxlength="3"
                    placeholder="Mr/Mrs"
                    onChange={(e) => myChangeHandler(e)}
                  />
                  <br></br>
                  <br></br>


                  <button
                    className="btn btn-primary btn-lg mx-auto"
                    type="submit"
