import React from 'react';
import SignaturePad from 'react-signature-canvas'
import styles from '../assets/styles.css'

class Popupsignature extends React.Component {
    state = {
        trimmedDataURL: null,

      };

      sigPad = {}
      clear = () => {
        this.sigPad.clear()
      }
      trim = () => {
        this.setState({
          trimmedDataURL: this.sigPad.getTrimmedCanvas()
            .toDataURL('image/png')
        })
      }
  render() { 
    let { trimmedDataURL } = this.state

    return (
        <div className="App">
            <div class="popup-overlay " style="position: fixed; top: 0px; bottom: 0px; left: 0px; right: 0px; background: rgba(0, 0, 0, 0.5); display: flex; z-index: 999;"><div class="popup-content " style="position: relative; background: rgb(255, 255, 255); width: 50%; margin: auto; border: 1px solid rgb(187, 187, 187); padding: 5px;"><canvas class="signatureCanvas" width="915" height="506" style="touch-action: none;"></canvas><button>Save</button><button>Clear</button><button>Close</button></div></div>
     <div className='signatureCanvas'>
        <div className='popup_inner'>
        <div className={styles.container}>
          <div className={styles.sigContainer}>
            <SignaturePad canvasProps={{ className: styles.sigPad }}
              ref={(ref) => { this.sigPad = ref }} />
          </div>
          <div>
            <button className={styles.buttons} onClick={this.clear}>
              Clear
        </button>
            <button className={styles.buttons} onClick={this.trim}>
              Trim
        </button>
          </div>

        </div>

        <button onClick={this.props.closePopup}>close me</button>
        </div>

        </div>
        {trimmedDataURL
            ? <img className={styles.sigImage}
              src={trimmedDataURL}
              style={{
                display: "block",
                margin: "0 auto",
                border: "1px solid black",
                width: "150px"
              }} />
            : null}
      </div>


    );
  }
}

export default Popupsignature;
