import React, { Component } from 'react';
import Draggable, { ControlPosition } from 'react-draggable'; 

class Dragsidebar extends Component {
    state = {
        activeDrags: 0,
        deltaPosition: {
          x: 0, y: 0
        },
        controlledPosition: {
          x: -400, y: 200
        }
      };

      handleDrag = (e, ui) => {
        const {x, y} = this.state.deltaPosition;
        this.setState({
          deltaPosition: {
            x: x + ui.deltaX,
            y: y + ui.deltaY,
          }
        });
      };

      onStart = () => {
        this.setState({activeDrags: ++this.state.activeDrags});
      };

      onStop = () => {
        this.setState({activeDrags: --this.state.activeDrags});
      };

      // For controlled component
      adjustXPos = (e) => {
        e.preventDefault();
        e.stopPropagation();
        const {x, y} = this.state.controlledPosition;
        this.setState({controlledPosition: {x: x - 10, y}});
      };

      adjustYPos = (e) => {
        e.preventDefault();
        e.stopPropagation();
        const {controlledPosition} = this.state;
        const {x, y} = controlledPosition;
        this.setState({controlledPosition: {x, y: y - 10}});
      };

      onControlledDrag = (e, position) => {
        const {x, y} = position;
        this.setState({controlledPosition: {x, y}});
      };

      onControlledDragStop = (e, position) => {
        this.onControlledDrag(e, position);
        this.onStop();
      };



      render() {
        const dragHandlers = {onStart: this.onStart, onStop: this.onStop};
        const {deltaPosition, controlledPosition} = this.state;
        return (
          <div>

            <Draggable handle="strong">
              <div className="box no-cursor" style={{display: 'flex', flexDirection: 'column'}}>
                <strong className="cursor"><div>Drag here</div></strong>
                <div style={{overflow: 'scroll'}}>
                  <div style={{background: 'yellow', whiteSpace: 'pre-wrap'}}>
                    I have long scrollable content with a handle
                  </div>
                </div>
              </div>
            </Draggable>
            <Draggable cancel="strong" {...dragHandlers}>
              <div className="box">
                <strong className="no-cursor">Can't drag here</strong>
                <div>Dragging here works</div>
              </div>
            </Draggable>
            <Draggable grid={[25, 25]} {...dragHandlers}>
              <div className="box">I snap to a 25 x 25 grid</div>
            </Draggable>
            <Draggable grid={[50, 50]} {...dragHandlers}>
              <div className="box">I snap to a 50 x 50 grid</div>
            </Draggable>


          </div>
        );
      }
}

export default Dragsidebar;
