import React from 'react';
import SignaturePad from 'react-signature-canvas'
import styles from '../assets/styles.css'

class Popupsignature extends React.Component {
    state = {
        trimmedDataURL: null,

    };

    sigCanvas= {}
    clear = () => {
        this.sigCanvas.current.clear()()
    }
    trim = () => {
        this.setState({
            trimmedDataURL: this.sigCanvas.current.getTrimmedCanvas()
                .toDataURL('image/png')
        })
    }

    save = () =>
    setImageURL(sigCanvas.current.getTrimmedCanvas().toDataURL("image/png"));
    render() {
        let { trimmedDataURL } = this.state

        return (
            <div className="App">
                <div className='signatureCanvas'>
                    <div className='popup_inner'>
                        <div className={styles.container}>
                            <div className={styles.sigContainer}>
                                <SignaturePad canvasProps={{ className: styles.sigCanvas}}
                                    ref={(ref) => { this.sigCanvas= ref }} />
                            </div>
                            <div>
                                <button onClick={this.clear}>
                                    Clear </button>
                                <button onClick={this.trim}>
                                   Save  </button>
                            </div>

                        </div>

                        <button onClick={this.props.closePopup}>close me</button>
                    </div>

                </div>
                {trimmedDataURL
                    ? <img className={styles.sigImage}
                        src={trimmedDataURL}
                        style={{
                            display: "block",
                            margin: "0 auto",
                            border: "1px solid black",
                            width: "150px"
                        }} />
                    : null}
            </div>


        );
    }
}

export default Popupsignature;
