import React from 'react';
import { Container, Header, Grid, Form, Popup } from 'semantic-ui-react';
import { Document, Page } from 'react-pdf';
import Homepreview from './components/Homepreview';
import '../src/assets/styles.css'
import Draggable, { ControlPosition } from 'react-draggable';
import ReactDOM from 'react-dom'
import SignaturePad from 'react-signature-canvas'
import styles from './assets/styles.css'
import Popupsignature from './components/Dragsidebar';
import SignatureCanvas from 'react-signature-canvas'

class App extends React.Component {
  state = {
    file: null,
    numPages: 0,
    pageNumber: 1,
    activeDrags: 0,
    deltaPosition: {
      x: 0, y: 0
    },
    controlledPosition: {
      x: -400, y: 200
    }
  };
  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }
  handleDrag = (e, ui) => {
    const { x, y } = this.state.deltaPosition;
    this.setState({
      deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      }
    });
  };

  onFileChange = (event) => {
    this.setState({
      file: event.target.files[0]
    });
  }

  onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages });
  }

  nextPage = () => {

    const currentPageNumber = this.state.pageNumber;
    let nextPageNumber;

    if (currentPageNumber + 1 > this.state.numPages) {
      nextPageNumber = 1;
    } else {
      nextPageNumber = currentPageNumber + 1;
    }

    this.setState({
      pageNumber: nextPageNumber
    });
  }
  sigPad = {}

  clearSig = () => {
      this.sigPad.clear();
      console.log('on click:', this.state.trimmedDataURL);
  }

  trim = () => {
      this.setState({ trimmedDataURL: this.sigPad.getTrimmedCanvas().toDataURL('image/png') })

  }
  state = {trimmedDataURL: null}
  sigPad = {}
  clear = () => {
    this.sigPad.clear()
  }
  trim = () => {
    this.setState({trimmedDataURL: this.sigPad.getTrimmedCanvas()
      .toDataURL('image/png')})
  }

  render() {
    const { pageNumber, numPages } = this.state;
    const dragHandlers = { onStart: this.onStart, onStop: this.onStop };
    let {trimmedDataURL} = this.state

    return (
      <main id="page-content-wrapper" role="main" className="container">
        {/* <Homepreview></Homepreview> */}
        <Header textAlign="center">PDF Preview</Header>
        <div>
          <Form>
            <div className="upload-btn">
              <input type="file" onChange={this.onFileChange} className="button-select">
              </input>
            </div>
          </Form>
          <div className={styles.container}>
      <div className={styles.sigContainer}>
        <SignaturePad canvasProps={{className: styles.sigPad}}
          ref={(ref) => { this.sigPad = ref }} />
      </div>
      <div>
        <button className={styles.buttons} onClick={this.clear}>
          Clear
        </button>
        <button className={styles.buttons} onClick={this.trim}>
          Trim
        </button>
      </div>
      {trimmedDataURL
        ? <img className={styles.sigImage} src={trimmedDataURL} />
        : null}
    </div>
    <Draggable
        axis="x"
        handle=".handle"
        defaultPosition={{x: 0, y: 0}}
        position={null}
        grid={[25, 25]}
        scale={1}
        onStart={this.handleStart}
        onDrag={this.handleDrag}
        onStop={this.handleStop}>
        <div>
          <div className="handle">Drag from here</div>
          <div>This readme is really dragging on...</div>
        </div>
      </Draggable>
          <div className="pagination-item">
            <Grid centered columns={2}>
              <Grid.Column textAlign="center" onClick={this.nextPage}>
                {this.state.file ? <div class="pagination">
                  <a href="#">&laquo;</a>
                  <a href="#" class="active">{pageNumber}</a>
                  <a href="#">of</a>
                  <a href="#">{numPages}</a>
                  <a href="#">&raquo;</a>
                </div> : null}

              </Grid.Column>
            </Grid>
          </div>

          <Draggable handle="strong" >
            <div className="box no-cursor" >
              <div>
                <Document file={this.state.file} onLoadSuccess={this.onDocumentLoadSuccess}
                  noData={<h4 className="success-file">Please select a file</h4>}>
                  <Page pageNumber={pageNumber} />
                </Document>
                {'\n' + Array(40).fill('').join('\n')}
              </div>
            </div>
          </Draggable>

          <div className="add-sidebar">
            <div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers}   >
                <div className="drag-item-box" onClick={this.togglePopup.bind(this)}>
                  <div>Signature</div>
                </div>
              </Draggable>
            </div>
            <div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers} >
                <div className="drag-item-box">
                  <div>Initials</div>
                </div>
              </Draggable>
            </div><div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers}>
                <div className="drag-item-box">
                  <div>Date</div>
                </div>
              </Draggable>
            </div>



          </div>
          <div>

            {this.state.showPopup ?
              <Popupsignature

                closePopup={this.togglePopup.bind(this)}
              />
              : null
            }

          </div>

        </div>


      </main>
    );
  }
}

export default App;
