import React from 'react';
import { Container, Header, Grid, Form } from 'semantic-ui-react';
import { Document, Page } from 'react-pdf';
import Homepreview from './components/Homepreview';
import '../src/assets/styles.css'
import Draggable, { ControlPosition } from 'react-draggable';

class App extends React.Component {

Homepreview() {
  const [imageURL, setImageURL] = useState(null); 
  const sigCanvas = useRef({});
  const clear = () => sigCanvas.current.clear();
  const save = () =>
    setImageURL(sigCanvas.current.getTrimmedCanvas().toDataURL("image/png"));

  return (
    <div className="App">
      <h1>Signature Pad Example</h1>
      <Popup
        modal
        trigger={<button>Open Signature Pad</button>}
        closeOnDocumentClick={false}
      >
        {close => (
          <>
            <SignaturePad
              ref={sigCanvas}
              canvasProps={{
                className: "signatureCanvas"
              }}
            />
            {/* Button to trigger save canvas image */}
            <button onClick={save}>Save</button>
            <button onClick={clear}>Clear</button>
            <button onClick={close}>Close</button>
          </>
        )}
      </Popup>
      <br />
      <br />

      {imageURL ? (
        <img
          src={imageURL}
          alt="my signature"
          style={{
            display: "block",
            margin: "0 auto",
            border: "1px solid black",
            width: "150px"
          }}
        />
      ) : null}
    </div>
  );
}
  state = {
    file: null,
    numPages: 0,
    pageNumber: 1,
    activeDrags: 0,
    deltaPosition: {
      x: 0, y: 0
    },
    controlledPosition: {
      x: -400, y: 200
    }
  };

  handleDrag = (e, ui) => {
    const { x, y } = this.state.deltaPosition;
    this.setState({
      deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      }
    });
  };

  onFileChange = (event) => {
    this.setState({
      file: event.target.files[0]
    });
  }

  onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages });
  }

  nextPage = () => {

    const currentPageNumber = this.state.pageNumber;
    let nextPageNumber;

    if (currentPageNumber + 1 > this.state.numPages) {
      nextPageNumber = 1;
    } else {
      nextPageNumber = currentPageNumber + 1;
    }

    this.setState({
      pageNumber: nextPageNumber
    });
  }



  onDrag() {
    alert('123')
  }


  render() {
    const { pageNumber, numPages } = this.state;
    const dragHandlers = { onStart: this.onStart, onStop: this.onStop };

    return (

      <main id="page-content-wrapper" role="main" className="container">
        <Homepreview></Homepreview>
        <Header textAlign="center">PDF Preview</Header>
        <div>
          <Form>
            <div className="upload-btn">
              <input type="file" onChange={this.onFileChange} className="button-select">
              </input>
            </div>
          </Form>

          <div className="pagination-item">
            <Grid centered columns={2}>
              <Grid.Column textAlign="center" onClick={this.nextPage}>
                {this.state.file ? <div class="pagination">
                  <a href="#">&laquo;</a>
                  <a href="#" class="active">{pageNumber}</a>
                  <a href="#">of</a>
                  <a href="#">{numPages}</a>
                  <a href="#">&raquo;</a>
                </div> : null}

              </Grid.Column>
            </Grid>
          </div>

          <Draggable handle="strong" >
            <div className="box no-cursor" >
              <div>
                <Document file={this.state.file} onLoadSuccess={this.onDocumentLoadSuccess}
                  noData={<h4 className="success-file">Please select a file</h4>}>
                  <Page pageNumber={pageNumber} />
                </Document>
                {'\n' + Array(40).fill('').join('\n')}
              </div>
            </div>
          </Draggable>

          <div className="add-sidebar">
            <div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers}   >
                <div className="drag-item-box" onClick={this.onDrag}>
                  <div>Signature</div>
                </div>
              </Draggable>
            </div>
            <div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers} >
                <div className="drag-item-box">
                  <div>Initials</div>
                </div>
              </Draggable>
            </div><div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers}>
                <div className="drag-item-box">
                  <div>Date</div>
                </div>
              </Draggable>
            </div>



          </div>

        </div>


      </main>
    );
  }
}

export default App;
