import React from 'react';
import { Container, Header, Grid, Form, Popup } from 'semantic-ui-react';
import { Document, Page } from 'react-pdf';
import Homepreview from './components/Homepreview';
import '../src/assets/styles.css'
import Draggable, { ControlPosition } from 'react-draggable';
import Popupsignature from './components/Dragsidebar';
import * as jsPDF from 'jspdf';
import * as html2canvas from 'html2canvas';

class App extends React.Component {
  state = {
    file: null,
    numPages: 0,
    pageNumber: 1,
    activeDrags: 0,
    deltaPosition: {
      x: 0, y: 0
    },
    controlledPosition: {
      x: -400, y: 200
    }
  };
  togglePopup() {
    this.setState({
      showPopup: !this.state.showPopup
    });
  }
  handleDrag = (e, ui) => {
    const { x, y } = this.state.deltaPosition;
    this.setState({
      deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      }
    });
  };

  onFileChange = (event) => {
    this.setState({
      file: event.target.files[0]
    });
  }

  onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages });
  }

  nextPage = () => {

    const currentPageNumber = this.state.pageNumber;
    let nextPageNumber;

    if (currentPageNumber + 1 > this.state.numPages) {
      nextPageNumber = 1;
    } else {
      nextPageNumber = currentPageNumber + 1;
    }

    this.setState({
      pageNumber: nextPageNumber
    });
  }
  // handlePdf = () => {
  //   const input = document.getElementById('page');

  //   html2canvas(input)
  //     .then((canvas) => {
  //       const imgData = canvas.toDataURL('image/png');
  //       const pdf = new jsPDF({
  //         orientation: 'landscape',
  //       });
  //       const imgProps = pdf.getImageProperties(imgData);
  //       const pdfWidth = pdf.internal.pageSize.getWidth();
  //       const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;
  //       pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
  //       pdf.save('download.pdf');
  //     });
  // };
   getPDF(){
    const input = document.getElementById('page');

    var top_left_margin = 15;

    var canvas_image_width = HTML_Width;
    var canvas_image_height = HTML_Height;

    var totalPDFPages = Math.ceil(HTML_Height/PDF_Height)-1;


      html2canvas(input)
      .then((canvas) => {
    canvas.getContext('2d');

    console.log(canvas.height+"  "+canvas.width);


    var imgData = canvas.toDataURL("image/jpeg", 1.0);
    var pdf = new jsPDF('p', 'pt',  [PDF_Width, PDF_Height]);
        pdf.addImage(imgData, 'JPG', top_left_margin, top_left_margin,canvas_image_width,canvas_image_height);


    for (var i = 1; i <= totalPDFPages; i++) { 
    pdf.addPage(PDF_Width, PDF_Height);
    pdf.addImage(imgData, 'JPG', top_left_margin, -(PDF_Height*i)+(top_left_margin*4),canvas_image_width,canvas_image_height);
    }

        pdf.save("HTML-Document.pdf");
           });
    };

  render() {
    const { pageNumber, numPages } = this.state;
    const dragHandlers = { onStart: this.onStart, onStop: this.onStop };
    return (
      <div>

     <main id="page-content-wrapper page" role="main" className="container">
       <Homepreview></Homepreview>
        <Header textAlign="center">PDF Preview</Header>
         <button onClick={this.handlePdf} className="btn btn-primary btn-lg mx-auto">Generate PDF</button>

        <div>
          <Form>
            <div className="upload-btn">
              <input type="file" onChange={this.onFileChange} className="button-select">
              </input>
            </div>
          </Form>

          <div className="pagination-item" >
            <Grid centered columns={2}>
              <Grid.Column textAlign="center" onClick={this.nextPage}>
                {this.state.file ? <div class="pagination">
                  <a href="#">&laquo;</a>
                  <a href="#" class="active">{pageNumber}</a>
                  <a href="#">of</a>
                  <a href="#">{numPages}</a>
                  <a href="#">&raquo;</a>
                </div> : null}
              </Grid.Column>
            </Grid>
          </div>

          <div id="page">
            <Draggable handle="strong" >
              <div className="box no-cursor" id="page" >
                <div>
                  <Document file={this.state.file} onLoadSuccess={this.onDocumentLoadSuccess}
                    noData={<h4 className="success-file">Please select a file</h4>}>
                    <Page pageNumber={pageNumber} />
                  </Document>
                </div>
              </div>
            </Draggable>
            <div className="add-sidebar">
            <div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers}   >
                <div className="drag-item-box" onClick={this.togglePopup.bind(this)}>
                  <div>Signature</div>
                </div>
              </Draggable>
            </div>
            <div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers} >
                <div className="drag-item-box">
                  <div>Initials</div>
                </div>
              </Draggable>
            </div><div className="drag-list">
              <Draggable cancel="strong" {...dragHandlers}>
                <div className="drag-item-box">
                  <div>Date</div>
                </div>
              </Draggable>
            </div>
          </div>
          </div>

          <div>
            {this.state.showPopup ?
              <Popupsignature
                closePopup={this.togglePopup.bind(this)}
              />
              : null
            }

          </div>

        </div>


      </main>
      </div>

    );
  }
}

export default App;
