import React, { Component } from 'react';
import Draggable, { ControlPosition } from 'react-draggable'; 

class Dragsidebar extends Component {
    state = {
        activeDrags: 0,
        deltaPosition: {
          x: 0, y: 0
        },
        controlledPosition: {
          x: -400, y: 200
        }
      };

      handleDrag = (e, ui) => {
        const {x, y} = this.state.deltaPosition;
        this.setState({
          deltaPosition: {
            x: x + ui.deltaX,
            y: y + ui.deltaY,
          }
        });
      };

      render() {
        const dragHandlers = {onStart: this.onStart, onStop: this.onStop};
        const {} = this.state;
        return (
            <div id="wrapper">

            <div id="sidebar-wrapper">
                <aside id="sidebar">
                    <ul id="sidemenu" class="sidebar-nav">
                        <li>
                            <a href="#">
                                <Draggable
                                onStop={(event, data) => this.onStop(event, data)}>
                                    <div className='handle'>Signature</div>
                                </Draggable>

                            </a>
                        </li>
                        <li>
                            <a href="#">
                                <Draggable
                                onStop={(event, data) => this.onStop(event, data)}>
                                    <div className='handle'>Initials</div>
                                </Draggable>

                            </a>
                        </li><li>
                            <a href="#">
                                <Draggable
                                onStop={(event, data) => this.onStop(event, data)}>
                                    <div className='handle'>Date</div>
                                </Draggable>

                            </a>
                        </li>

            <Draggable cancel="strong" {...dragHandlers}>
              <div className="box">
                <strong className="no-cursor">Can't drag here</strong>
                <div>Dragging here works</div>
              </div>
            </Draggable>
            <Draggable grid={[25, 25]} {...dragHandlers}>
              <div className="box">I snap to a 25 x 25 grid</div>
            </Draggable>
            <Draggable grid={[50, 50]} {...dragHandlers}>
              <div className="box">I snap to a 50 x 50 grid</div>
            </Draggable>
                    </ul>
                </aside>
            </div>
            <main id="page-content-wrapper" role="main">
            <Draggable handle="strong">
              <div className="box no-cursor" style={{display: 'flex', flexDirection: 'column'}}>
                <div style={{overflow: 'scroll'}}>
                  <div style={{background: 'yellow', whiteSpace: 'pre-wrap'}}>
                    I have long scrollable content with a handle
                    {'\n' + Array(40).fill('').join('\n')}
                  </div>
                </div>
              </div>
            </Draggable>
            </main>
        </div>


        );
      }
}

export default Dragsidebar;
