class Popupsignature extends React.Component {

    state = {
        trimmedDataURL: null,
      };

      sigPad = {}
      clear = () => {
        this.sigPad.clear()
      }

      trim = () => {
        this.setState({
          trimmedDataURL: this.sigPad.getTrimmedCanvas()
            .toDataURL('image/png')
        })
      }

  render() { 
    let { trimmedDataURL } = this.state

    return (
      <div className='popup'>
             <Popup
        modal trigger={<button>Open Signature Pad</button>}
        closeOnDocumentClick={false}> 
       <>
            <SignaturePad
             ref={(ref) => { this.sigPad = ref }} 
              canvasProps={{
                className: "signatureCanvas"
              }}
            />
          <button onClick={this.trim}>Save</button>
          <button onClick={this.props.closePopup}>close me</button>
          </>


      </Popup>
      <br>
      </br>
      {trimmedDataURL ? (
        <img
          src={trimmedDataURL}
          alt="my signature"
          style={{
            display: "block",
            margin: "0 auto",
            border: "1px solid black",
            width: "150px"
          }}
        />
      ) : null}


      </div>
    );
  }
}

export default Popupsignature;
