import React from 'react';
import { Container, Header, Grid, Form } from 'semantic-ui-react';
import { Document, Page } from 'react-pdf';
import Homepreview from './components/Homepreview';
// import { Draggable, Droppable } from 'react-drag-and-drop'
import '../src/assets/styles.css'
import Dragsidebar from './components/Dragsidebar';
import Draggable, { ControlPosition } from 'react-draggable';

class App extends React.Component {
  state = {
    file: null,
    numPages: 0,
    pageNumber: 1,
    activeDrags: 0,
    deltaPosition: {
      x: 0, y: 0
    },
    controlledPosition: {
      x: -400, y: 200
    }
  };

  handleDrag = (e, ui) => {
    const { x, y } = this.state.deltaPosition;
    this.setState({
      deltaPosition: {
        x: x + ui.deltaX,
        y: y + ui.deltaY,
      }
    });
  };

  onFileChange = (event) => {
    this.setState({
      file: event.target.files[0]
    });
  }

  onDocumentLoadSuccess = ({ numPages }) => {
    this.setState({ numPages });
  }

  nextPage = () => {

    const currentPageNumber = this.state.pageNumber;
    let nextPageNumber;

    if (currentPageNumber + 1 > this.state.numPages) {
      nextPageNumber = 1;
    } else {
      nextPageNumber = currentPageNumber + 1;
    }

    this.setState({
      pageNumber: nextPageNumber
    });
  }


  render() {
    const { pageNumber, numPages } = this.state;
    const dragHandlers = { onStart: this.onStart, onStop: this.onStop };

    return (
      <div id="wrapper">

        <div id="sidebar-wrapper">
          <aside id="sidebar">
            <ul id="sidemenu" class="sidebar-nav">
              <li>
                <a href="#">
                <Draggable cancel="strong" {...dragHandlers}>
                <div className="box">
                  <strong className="no-cursor">Can't drag here</strong>
                  <div>Dragging here works</div>
                </div>
              </Draggable>
                </a>
              </li>
              <li>
                <a href="#">
                  <Draggable
                    onStop={(event, data) => this.onStop(event, data)}>
                    <div className='handle'>Initials</div>
                  </Draggable>

                </a>
              </li><li>
                <a href="#">
                  <Draggable
                    onStop={(event, data) => this.onStop(event, data)}>
                    <div className='handle'>Date</div>
                  </Draggable>

                </a>
              </li>

            </ul>
          </aside>
        </div>
        <main id="page-content-wrapper" role="main" className="container">

          <Header textAlign="center">PDF Preview</Header>
          <div> <Form>
            <input type="file" onChange={this.onFileChange} className="button-select">
            </input>
          </Form>
            <Draggable handle="strong">

              <div className="box no-cursor" >
                <div >
                  <Document file={this.state.file} onLoadSuccess={this.onDocumentLoadSuccess} noData={<h4>Please select a file</h4>}>
                    <Page pageNumber={pageNumber} />
                  </Document>
                  {'\n' + Array(40).fill('').join('\n')}
                </div>
              </div>
            </Draggable>
            <div className="add-sidebar">

              <Draggable grid={[25, 25]} {...dragHandlers}>
                <div className="box">I snap to a 25 x 25 grid</div>
              </Draggable>
              <Draggable grid={[50, 50]} {...dragHandlers}>
                <div className="box">I snap to a 50 x 50 grid</div>
              </Draggable>
            </div>

          </div>


          <Grid centered columns={2}>
            <Grid.Column textAlign="center" onClick={this.nextPage}>
              {this.state.file ? <div class="pagination">
                <a href="#">&laquo;</a>
                <a href="#" class="active">{pageNumber}</a>
                <a href="#">{numPages}</a>
                <a href="#">&raquo;</a>
              </div> : null}

            </Grid.Column>
          </Grid>

        </main>
      </div>

    );
  }
}

export default App;
